mapper.insert:
INSERT INTO room_book (rb_uid,rb_rid,rb_bookdate)
VALUES(
	(SELECT `user`.u_id
		FROM `user`
		WHERE `user`.u_username=#{username}
		),
		(
		SELECT room.r_id
		FROM room
		WHERE room.r_num=#{num}
		),#{date}

)

mapper.select:
SELECT * FROM room_book
WHERE room_book.rb_uid=(
	SELECT u_id
	FROM `user`
	WHERE `user`.u_username=#{u_username}
)
